import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

function App() {
  const globeRef = useRef(null);

  useEffect(() => {
    const svg = d3.select(globeRef.current);

    const width = 400;
    const height = 400;

    // Projection du globe
    const projection = d3.geoOrthographic()
      .scale(190)
      .translate([width / 2, height / 2]);

    const path = d3.geoPath().projection(projection);

    // Chargement des données du monde
    d3.json("https://unpkg.com/world-atlas@2/world/110m.json").then(data => {
      const countries = d3.geoFeature(data, data.objects.countries);

      // Dessiner les pays
      svg.selectAll("path")
        .data(countries.features)
        .enter().append("path")
        .attr("class", "country")
        .attr("d", path);
    });

    // Rotation du globe
    d3.timer(elapsed => {
      const rotate = projection.rotate();
      rotate[0] += 0.25;
      projection.rotate(rotate);
      svg.selectAll("path").attr("d", path);
    });

  }, []);

  return (
    <div className="App">
      <svg ref={globeRef} width={400} height={400}></svg>
    </div>
  );
}

export default App;
