import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';
import * as topojson from 'topojson-client';

function App() {
  const globeRef = useRef(null);
  const isDragging = useRef(false);

  useEffect(() => {
    const svg = d3.select(globeRef.current);

    const width = 400;
    const height = 400;

    // Projection du globe
    const projection = d3.geoOrthographic()
      .scale(190)
      .translate([width / 2, height / 2]);

    const path = d3.geoPath().projection(projection);

    // Chargement des données du monde
    d3.json("https://unpkg.com/world-atlas@2.0.2/countries-110m.json").then(data => {
      const countries = topojson.feature(data, data.objects.countries);

      // Dessiner les pays
      svg.selectAll("path")
        .data(countries.features)
        .enter().append("path")
        .attr("class", "country")
        .attr("d", path)
        .on("mousedown", () => {
          isDragging.current = true;
        });

      svg.on("mousemove", function(event) {
        if (isDragging.current) {
          const dx = event.movementX;
          const dy = event.movementY;
          const rotate = projection.rotate();
          const scaleFactor = 0.25;

          rotate[0] += dx * scaleFactor;
          rotate[1] -= dy * scaleFactor;

          projection.rotate(rotate);
          svg.selectAll("path").attr("d", path);
        }
      });

      svg.on("mouseup", () => {
        isDragging.current = false;
      });
    });

    // Rotation du globe
    d3.timer(elapsed => {
      const rotate = projection.rotate();
      rotate[0] += 0.15;
      projection.rotate(rotate);
      svg.selectAll("path").attr("d", path);
    });

  }, []);

  return (
    <div className="App">
      <svg ref={globeRef} width={400} height={400}></svg>
    </div>
  );
}

export default App;
